	public String[][] select(int month) {
		String[][] columns;
		int i = 0;
		Cursor c =  db.rawQuery("select day, word1, word2, word3, word4 from words where month = " + month, null);
		if (c.moveToFirst()) {
			columns = new String[c.getCount()][5];
			do {
				for (int j = 0; j < 5; j++) {
					columns[i][j] = c.getString(j);
				}
				i++;
			} while (c.moveToNext());
		} else {
			columns = null;
		}
		c.close();
		return columns;
	}
	
